using UnityEngine;using UnityEngine.UI;using System.Collections;public class ConnectionLostWindow : MonoBehaviour {    public void DisplayWindow()    {        this.gameObject.GetComponent<CanvasGroup>().alpha = 1;        this.gameObject.GetComponent<CanvasGroup>().blocksRaycast = true;    }    public void HideWindow()    {        this.gameObject.GetComponent<CanvasGroup>().alpha = 0;        this.gameObject.GetComponent<CanvasGroup>().blockRaycast = false;    }	void Update () {        if (!SocketManager.instance.connected)            DisplayWindow();        else            HideWindow();	}}